INSERT INTO outcomes (outcome)
VALUES
    ('Оцінювати та вибирати необхідні для вирішення професійних завдань інформаційно-довідникові ресурси.'),
    ('Знати кодекс професійної етики, розуміти соціальну значимість та культурні аспекти інженерії програмного забезпечення і дотримуватись їх в професійній діяльності.'),
    ('Розрізняти основні процеси, фази та ітерації життєвого циклу програмного забезпечення.'),
    ('Застосовувати професійні стандарти й інші нормативно-правові документи в галузі інженерії програмного забезпечення.'),
    ('Застосовувати відповідні математичні поняття, методи доменного, системного і об’єктно-орієнтованого аналізу та математичного моделювання для розробки програмного забезпечення.'),
    ('Вибирати та використовувати відповідну задачі методологію створення програмного забезпечення.'),
    ('Пояснювати та застосовувати на практиці фундаментальні концепції, парадигми і основні принципи функціонування мовних, інструментальних і обчислювальних засобів інженерії програмного забезпечення.'),
    ('Аналізувати вимоги до інтерфейсу програмного забезпечення та вибирати відповідні вимогам засоби його реалізації.'),
    ('Збирати, формулювати та аналізувати вимоги до програмного забезпечення.'),
    ('Проводити передпроектне обстеження предметної області, системний аналіз об\'єкта проектування, вибирати вихідні дані для проектування, керуючись формальними методами опису вимог та моделювання.'),
    ('Оцінювати підходи до проектування програмного забезпечення та вибирати найбільш ефективні.'),
    ('Застосовувати знання про алгоритми та структури даних для розробки програмного забезпечення.'),
    ('Застосовувати на практиці інструментальні програмні засоби доменного аналізу, проектування, тестування, візуалізації, вимірювань та документування програмного забезпечення.'),
    ('Вибирати мови програмування та технології розробки для розв’язання завдань створення і супроводження програмного забезпечення.'),
    ('Демонструвати навички командної розробки, погодження, оформлення і випуску всіх видів програмної документації.'),
    ('Застосовувати методи компонентної розробки програмного забезпечення.'),
    ('Вибирати відповідні поставленій задачі структури даних і методи їхньої обробки, зберігання та передачі.'),
    ('Описувати методи верифікації та валідації програмного забезпечення.'),
    ('Оцінювати та забезпечувати якість програмного забезпечення.'),
    ('Описувати засоби забезпечення інформаційної безпеки (в тому числі кібербезпеки) і цілісності даних, вибирати та застосовувати їх відповідно до розв\'язуваних прикладних завдань та створюваних програмних систем.'),
    ('Використовувати принципи та практики проєктного менеджменту для розробки, планування, впровадження та управління проєктом, забезпечувати своєчасне досягнення його мети.'),
    ('Розробляти та створювати технічну документацію та презентацію програмного забезпечення, які придатні для відповідної аудиторії.'),
    ('Розуміти основи розрахунку економічної ефективності програмних систем.'),
    ('Проводити аналіз функціонування та розвитку суб’єктів господарювання, визначати функціональні сфери, розраховувати відповідні показники, які характеризують результативність їх діяльності.'),
    ('Використовувати основи бізнес-планування та оцінювання кон’юнктури ринків та результатів діяльності для забезпечення діяльності підприємницьких структур із урахуванням ризиків.'),
    ('Інтерпретувати неповну інформацію для проєктування, приймання та аргументування інноваційних рішень.'),
    ('Визначати пріоритетність завдань організації в рамках стратегічних цілей та узгоджувати їх із персоналом.'),
    ('Вибирати та застосовувати управлінські засоби та прийоми для забезпечення організаційних цілей, розвитку персоналу та зміцнення самої організації.'),
    ('Використовувати кількісні та якісні методи дослідження для аналізування даних та інформації, що сприяє надійному і узгодженому прийняттю рішень.'),
    ('Ідентифікувати себе як члена громадянського суспільства та наукової спільноти, визнавати верховенство права, використовувати власні права та свободи, виявляти повагу до прав і свобод інших осіб, зокрема, членів колективу.'),
    ('Розділяти моральні, культурні та наукові цінності, цінувати досягнення суспільства в соціально-економічній і технологічній сфері задля сталого розвитку, підтримувати ведення здорового способу життя.'),
    ('Демонструвати навички міжособистісної взаємодії, використовувати професійну аргументацію, емоціональний інтелект і емпатію для обміну інформацією, ідеями, задачами та методами їх розв’язання як з фахівцями, так і з нефахівцями.'),
    ('Усвідомлювати основні особливості сучасної світової та національної економіки, інституційної структури, напрямів соціальної, економічної та зовнішньоекономічної політики держави.'),
    ('Визначати та планувати можливості особистого професійного розвитку.'),
    ('Демонструвати навички креативного та критичного мислення у дослідженнях та професійному спілкуванні.'),
    ('Демонструвати навички усної та письмової професійної комунікації державною та іноземною мовами.');
